mapper.select:

SELECT RPE_HASH_NO, RPE_NO, RPE_HASH_TAG
FROM RECIPE_HASH
WHERE RPE_NO=#{value}


SELECT INGR_NO, RPE_NO, INGR_NAME, INGR_MAIN_CHECK, INGR_QUAN
FROM RECIPE_INGR
WHERE RPE_NO=#{value}

SELECT RPE_NO,RPE_IMG,RPE_TITLE,RPE_LIKE,RPE_HIT,
TO_CHAR(RPE_DATE,'YYYY/MM/DD')RPE_DATE FROM RECIPE WHERE
USER_NO=#{value}
